# BST Insert Function
class Node:
def __init__(self, value):
self.value = value
self.left = None
	 self.right = None
	def insert(root, value):
if root is None:
return Node(value)
if value < root.value:
root.left = insert(root.left, value)
else:
root.right = insert(root.right, value)
return root

